import{_ as c,c as o,a0 as d,o as s}from"./chunks/framework.UCJAoCzn.js";const p=JSON.parse('{"title":"CSS 与 CSS3","description":"","frontmatter":{},"headers":[],"relativePath":"css/css-and-css3.md","filePath":"css/css-and-css3.md"}'),a={name:"css/css-and-css3.md"};function S(t,e,i,l,r,n){return s(),o("div",null,e[0]||(e[0]=[d('<h1 id="css-与-css3" tabindex="-1">CSS 与 CSS3 <a class="header-anchor" href="#css-与-css3" aria-label="Permalink to &quot;CSS 与 CSS3&quot;">​</a></h1><h2 id="什么是-css" tabindex="-1">什么是 CSS？ <a class="header-anchor" href="#什么是-css" aria-label="Permalink to &quot;什么是 CSS？&quot;">​</a></h2><p>在前端 <code>CSS</code> 指的是叠层样式表（Cascading Style Sheets）的简称。</p><p><code>CSS</code> 是一种用于描述网页样式的语言，它允许开发者为网页添加颜色、字体、布局、背景图片、动画、阴影等效果。</p><p><code>CSS</code> 的作用是让网页具有更好的外观和感觉，让网页更美观、更符合用户的期待。</p><p><code>CSS</code> 的由 W3C 组织管理，由 W3C 官方发布，由 W3C 官方维护。</p><p><code>CSS</code> 由三部分组成：<code>选择器</code>、<code>属性</code>、<code>值</code>。</p><h2 id="什么是-css3" tabindex="-1">什么是 CSS3？ <a class="header-anchor" href="#什么是-css3" aria-label="Permalink to &quot;什么是 CSS3？&quot;">​</a></h2><p><code>CSS3</code> 是 <code>CSS</code> 的最新版本，它提供了许多新的特性，如动画、阴影、渐变、字体、媒体查询、flexbox、grid 等。</p><p><code>CSS</code> 有效的<code>选择器</code>、<code>属性</code>和<code>值</code>在 <code>CSS3</code> 中也同样有效。</p><p>对于了解 <code>CSS</code> 的人来说，学习 <code>CSS3</code> 只是需要继续学习一些新的 <code>选择器</code>、<code>属性</code>和<code>值</code>而已。</p><h2 id="css3-的主要特点" tabindex="-1">CSS3 的主要特点 <a class="header-anchor" href="#css3-的主要特点" aria-label="Permalink to &quot;CSS3 的主要特点&quot;">​</a></h2><ul><li>提供了更加强大精准的 <code>选择器</code></li><li>提供了多种 <code>背景填充</code> 方式</li><li>实现了 <code>颜色渐变</code></li><li>可以改变元素的 <code>形状</code> 和 <code>角度</code></li><li>可以加深阴影效果、grid布局、弹性盒模型、IE6混杂模式</li><li>新的计量单位</li><li>动画效果</li></ul>',13)]))}const h=c(a,[["render",S]]);export{p as __pageData,h as default};
