import{_ as l,c as t,a2 as a,o as e}from"./chunks/framework.BLTIpkzl.js";const r="/assets/image-1.lN_4DD7_.png",o="/assets/image.CwWQDrph.png",x=JSON.parse('{"title":"Linux 目录","description":"","frontmatter":{},"headers":[],"relativePath":"linux/linux-directory.md","filePath":"linux/linux-directory.md"}'),n={name:"linux/linux-directory.md"};function s(d,i,u,c,p,m){return e(),t("div",null,i[0]||(i[0]=[a('<h1 id="linux-目录" tabindex="-1">Linux 目录 <a class="header-anchor" href="#linux-目录" aria-label="Permalink to &quot;Linux 目录&quot;">​</a></h1><p>linux 遵循 FHS 协议，统一 linux根目录</p><h2 id="fhs协议" tabindex="-1">FHS协议 <a class="header-anchor" href="#fhs协议" aria-label="Permalink to &quot;FHS协议&quot;">​</a></h2><p>FHS(Filesystem Hierarchy Standard)，文件系统层次化标准</p><h2 id="根目录下文件夹" tabindex="-1">根目录下文件夹 <a class="header-anchor" href="#根目录下文件夹" aria-label="Permalink to &quot;根目录下文件夹&quot;">​</a></h2><p><img src="'+r+'" alt="alt text"></p><ul><li>1）usr：用户程序，与软件安装有关</li><li>2）var：变量文件，和系统运行相关</li><li>3）bin：用户二进制文件，储存可执行文件，【相当于windows环境变量中的命令】</li><li>4）boot：引导加载程序文件，和系统开机相关【如：reboot重启】</li><li>5）dev：设备文件，以文件的形式存在此目录下，包括终端设备、usb或连接到系统的任何设备</li><li>6）etc：配置文件，存放程序所需的配置文件</li><li>7）home：用户目录，存储所有用户的个人档案【除了root用户】</li><li>8）root：root用户主目录</li><li>9）lib：系统库/函数库，存放系统函数</li><li>10）media：可移动媒体设备，存放着可移动设备的临时目录【光盘，u盘，dvd盘】</li><li>11）mnt：挂载目录，和media类似</li><li>12）opt：可选的附加应用程序，存放第三方软件目录，我们写的程序可以放在这里</li><li>13）sbin：系统二进制文件，只有系统管理员进行访问</li><li>14）srv：服务数据，存放用户主动生产的数据</li><li>15）tmp：临时文件，包含系统和用户的临时文件</li><li>16）proc：进程信息，虚拟文件系统不存在硬盘中，而是在内存中，关机后消失，存放系统内核、进程、外部设备的状态</li><li>17）sys：存放和系统相关的信息</li><li>18）Lost+found：有的系统没有，用于当文件系统发生错误时，将丢失问问捡碎片存在这里</li></ul><p><img src="'+o+'" alt="alt text"></p>',8)]))}const _=l(n,[["render",s]]);export{x as __pageData,_ as default};
